mapper.select:
select 

from platform


		platform_id = #{platform_id,jdbcType=INTEGER}


		and platform_name like concat('%',#{platform_name,jdbcType=VARCHAR},'%')


		and status = #{status,jdbcType=TINYINT}


	    and platform_code = #{platform_code,jdbcType=VARCHAR}

select
platform_id,platform_name
from platform


  platform_name = #{platform_name,jdbcType=VARCHAR}


  and platform_id = #{platform_id,jdbcType=INTEGER}
