mapper.select:
  	select 
  	 	paqm.*, p.product_name, qc.product_qc_code, qc.product_qc_name
	from product_apply_qc_mapping paqm
	left join product p on p.product_id = paqm.product_id
	left join product_qc qc on qc.product_qc_id = paqm.product_qc_id and qc.status in ('INIT','OK')


			paqm.product_apply_id = #{product_apply_id, jdbcType=INTEGER}


			and paqm.product_id = #{product_id, jdbcType=INTEGER}


			and paqm.product_apply_qc_mapping_id = #{product_apply_qc_mapping_id, jdbcType=INTEGER}

mapper.delete:
delete from product_apply_qc_mapping


    product_apply_qc_mapping_id = #{productApplyQcMappingId,jdbcType=INTEGER}


    and product_apply_id = #{productApplyId,jdbcType=INTEGER}


    and product_qc_id = #{productQcId,jdbcType=INTEGER}


    and product_id = #{productId,jdbcType=INTEGER}

mapper.update:
update product_apply_qc_mapping
set product_apply_id = #{productApplyId,jdbcType=INTEGER},
  product_qc_id = #{productQcId,jdbcType=INTEGER},
  product_id = #{productId,jdbcType=INTEGER}
where product_apply_qc_mapping_id = #{productApplyQcMappingId,jdbcType=INTEGER}
