mapper.select:
  	select 

  	from product_tag


  			and product_tag_id = #{product_tag_id, jdbcType=INTEGER}


  			and product_tag_name = #{product_tag_name, jdbcType=VARCHAR}


  			and tag_type = #{tag_type, jdbcType=INTEGER}


  			and parent_id = #{parent_id, jdbcType=INTEGER}


  			and status = #{status, jdbcType=VARCHAR}


			and created_time >= #{start_time,jdbcType=TIMESTAMP}


			and created_time <= #{end_time,jdbcType=TIMESTAMP}

	select 
		p.*,s.product_tag_id as child_tag_id, s.product_tag_name as child_tag_name, s.tag_type as child_tag_type, s.parent_id as child_parent_id, s.status as child_status
	from product_tag p
	left join product_tag s on s.parent_id = p.product_tag_id 

  		p.tag_type = 1 

  			and p.product_tag_id = #{product_tag_id, jdbcType=INTEGER}


  			and p.product_tag_name like CONCAT('%', #{product_tag_name, jdbcType=VARCHAR}, '%')


  			and p.parent_id = #{parent_id, jdbcType=INTEGER}


  			and p.status = #{status, jdbcType=VARCHAR}


			and p.created_time >= #{start_time,jdbcType=TIMESTAMP}


			and p.created_time <= #{end_time,jdbcType=TIMESTAMP}

mapper.insert:
insert into product_tag (product_tag_id, product_tag_name, tag_type, 
  parent_id, status, created_user, 
  created_time, last_updated_user, last_updated_time
  )
values (#{productTagId,jdbcType=INTEGER}, #{productTagName,jdbcType=VARCHAR}, #{tagType,jdbcType=INTEGER}, 
  #{parentId,jdbcType=INTEGER}, #{status,jdbcType=VARCHAR}, #{createdUser,jdbcType=VARCHAR}, 
  #{createdTime,jdbcType=TIMESTAMP}, #{lastUpdatedUser,jdbcType=VARCHAR}, #{lastUpdatedTime,jdbcType=TIMESTAMP}
  )
