mapper.select:
SELECT 
	pa.*, 
	pt.party_name, 
	if(paqm.product_apply_id is null, 0, 1) as is_qc_mapped,
	m.merchant_name
FROM product_apply pa
LEFT JOIN party pt on pt.party_id = pa.party_id and pt.status="OK"
LEFT JOIN product p on p.product_apply_id = pa.product_apply_id
LEFT JOIN product_apply_qc_mapping paqm on paqm.product_apply_id = pa.product_apply_id
left join merchant m on m.merchant_id = pa.merchant_id and m.status ='OK'

	pa.status = 'OK'

		and pa.product_apply_id = #{product_apply_id,jdbcType=INTEGER}


		and pa.party_id = #{party_id,jdbcType=INTEGER}


		and pa.product_apply_name like CONCAT('%', #{product_apply_name,jdbcType=VARCHAR},'%')


		and pa.product_apply_name = #{product_apply_actual_name}


		and pa.product_apply_code = #{product_apply_code, jdbcType=VARCHAR}


		and pa.apply_status = #{apply_status,jdbcType=CHAR}


		and pa.created_time >= #{start_time,jdbcType=TIMESTAMP}


		and pa.created_time <= #{end_time,jdbcType=TIMESTAMP}


		and p.status = #{product_status}


		and pa.check_time >= #{start_check_time}


		and pa.check_time <= #{end_check_time}


GROUP BY pa.product_apply_id

select 
	count(*) as count,
	p.party_id,
	p.party_name
from product_apply pa
inner join party p on p.party_id = pa.party_id and p.`status`='OK'
where pa.apply_status = #{status}

	and pa.party_id = #{party_id} 


	and pa.check_time >= #{start_check_time}


	and pa.check_time <= #{end_check_time}

group by pa.party_id
