mapper.select:
   	SELECT 
   		pgm.*,
   		g.goods_name,
   		pf.platform_name,
   		m.merchant_name
   	from product_goods_mapping pgm 
	LEFT JOIN goods g on g.goods_id = pgm.goods_id
	left join platform pf on pgm.platform_id = pf.platform_id and pf.status='OK'
	left join merchant m on pgm.merchant_id = m.merchant_id and m.status='OK'


			pgm.product_id = #{product_id,jdbcType=INTEGER}


			and pgm.merchant_id = #{merchant_id,jdbcType=INTEGER}


			and pgm.goods_id = #{goods_id,jdbcType=BIGINT}


			and pgm.status = #{status,jdbcType=CHAR}


			and pgm.created_time >= #{start_time,jdbcType=TIMESTAMP}


			and pgm.created_time <= #{end_time,jdbcType=TIMESTAMP}


			and pgm.created_user = #{created_user,jdbcType=VARCHAR}
