mapper.select:
select
pp.*,a.area_name areaName
from purchase_place pp
inner join area a on a.area_id = pp.area_id


  pp.created_time between #{start_time} and #{end_time}


  pp.created_time >= #{start_time}


  pp.created_time <= #{end_time}


  and  a.area_id = #{area_id,jdbcType=INTEGER}


  and pp.purchase_place_name = #{purchase_place_name,jdbcType=VARCHAR}
